// 泛型类
class DataStorage<T> {
  private items: T[] = [];

  addItem(item: T) {
    this.items.push(item);
  }

  removeItem(item: T) {
    this.items = this.items.filter(i => i !== item);
  }

  getItems(): T[] {
    return [...this.items];
  }
}

const textStorage = new DataStorage<string>();
textStorage.addItem("Alice");
textStorage.addItem("Bob");
textStorage.removeItem("Alice");
