}

const Diagonal = {
  [Symbol.for("Up-Left")]: "UP-LEFT",
  [Symbol.for("Up-Right")]: "UP-RIGHT",
  [Symbol.for("Down-Left")]: "DOWN-LEFT",
  [Symbol.for("Down-Right")]: "DOWN-RIGHT"
};

