let input = [1, 2];
let [first, second] = input;
console.log(first); // 输出 1
console.log(second); // 输出 2

let [first, , third] = [1, 2, 3];
console.log(first); // 输出 1
console.log(third); // 输出 3

let [first, ...rest] = [1, 2, 3, 4];
console.log(first); // 输出 1
console.log(rest); // 输出 [2, 3, 4]

let o = { a: 1, b: 2 };
let { a, b } = o;
console.log(a); // 输出 1
console.log(b); // 输出 2

let { a: newName1, b: newName2 } = o;
console.log(newName1); // 输出 1
console.log(newName2); // 输出 2

let { a, b = 10 } = { a: 5 };
console.log(a); // 输出 5
console.log(b); // 输出 10

let [first, second = 10] = [1];
console.log(first); // 输出 1
console.log(second); // 输出 10

let nested = { a: { b: 1 } };
let {
  a: { b },
} = nested;
console.log(b); // 输出 1

let nestedArray = [1, [2, 3]];
let [first, [second, third]] = nestedArray;
console.log(first); // 输出 1
console.log(second); // 输出 2
console.log(third); // 输出 3

function f([first, second]: [number, number]) {
  console.log(first);
  console.log(second);
}
f([1, 2]); // 输出 1 和 2

function g({ a, b }: { a: number; b: number }) {
  console.log(a);
  console.log(b);
}
g({ a: 1, b: 2 }); // 输出 1 和 2

interface User {
  id: number;
  name: string;
  age?: number; // age 是可选属性
}

function displayUser({ id, name, age = 30 }: User) {
  console.log(`ID: ${id}`);
  console.log(`Name: ${name}`);
  console.log(`Age: ${age}`);
}

const user: User = { id: 1, name: 'Alice' };
displayUser(user); // 输出 ID: 1, Name: Alice, Age: 30

interface Point {
  x: number;
  y: number;
}

function logPoint({ x, y }: Point): void {
  console.log(`x = ${x}, y = ${y}`);
}

const point = { x: 10, y: 20 };
logPoint(point); // 输出 x = 10, y = 20
