enum Color {
  Red,
  Green,
  Blue,
}

namespace Color {
  export function isPrimaryColor(color: Color): boolean {
    return color === Color.Red || color === Color.Blue;
  }
}

console.log(Color.isPrimaryColor(Color.Red)); // 输出: true
console.log(Color.isPrimaryColor(Color.Green)); // 输出: false
