}

// person.d.ts

/**
 * 接口 Person 描述一个人的基本信息
 */
interface Person {
  /**
   * 姓名
   */
  name: string;

  /**
   * 年龄
   */
  age: number;

  /**
   * 问候方法
   */
  greet(): void;
}

// Usage example

const person: Person = {
  name: "Alice",
  age: 30,
  greet() {
    console.log(`Hello, my name is ${this.name}.`);
  }
};

person.greet(); // 调用接口方法

