type Coordinates = {
  x: number;
  y: number;
};

// coordinates.d.ts

/**
 * 类型别名 Coordinates 描述一个坐标点
 */
type Coordinates = {
  /**
   * x 坐标
   */
  x: number;

  /**
   * y 坐标
   */
  y: number;
};

// Usage example

function printCoordinates(coords: Coordinates) {
  console.log(`Coordinates: (${coords.x}, ${coords.y})`);
}

const point: Coordinates = { x: 10, y: 20 };
printCoordinates(point); // 使用类型别名作为参数类型

