// animal.d.ts

/**
 * 类 Animal 描述一个动物
 */
declare class Animal {
  /**
   * 构造函数，创建一个动物实例
   * @param name 动物的名字
   */
  constructor(name: string);

  /**
   * 属性：名字
   */
  name: string;

  /**
   * 方法：吃东西
   * @param food 食物的名字
   */
  eat(food: string): void;
}

// Usage example

const cat = new Animal("Kitty");
cat.eat("fish"); // 调用实例方法
console.log(cat.name); // 访问属性
