props.code:
button {
  background: none;
  border: none;
  color: inherit;
  font: inherit;
  outline: none;
  padding: 0;
}

/* 添加边框 */
.nav li {
  border-right: 1px solid #666;
}

/* 去掉边框 */
.nav li:last-child {
  border-right: none;
}

@font-face {
  font-family: 'Dank Mono';
  src:
    /* Full name */ local('Dank Mono'), /* Postscript name */ local('Dank-Mono'),
    /* Otherwise, download it! */ url('//...a.server/fonts/DankMono.woff');
}

code {
  font-family: 'Dank Mono', system-ui-monospace;
}

a:focus,
button:focus,
input:focus,
select:focus,
textarea:focus {
  box-shadow: none;
  outline: #000 dotted 2px;
  outline-offset: 0.05em;
}

html,
body {
  height: 100%;
  margin: 0;
}

body {
  -webkit-align-items: center;
  -ms-flex-align: center;
  align-items: center;
  display: -webkit-flex;
  display: flex;
}

body {
  display: grid;
  height: 100vh;
  margin: 0;
  place-items: center center;
}

li {
  display: none;
}

/* 选择第 1 至第 3 个元素并显示出来 */
li:nth-child(-n + 3) {
  display: block;
}

/* 选择除前3个之外的所有项目，并显示它们 */
li:not(:nth-child(-n + 3)) {
  display: none;
}

.slider {
  max-height: 200px;
  overflow-y: hidden;
  width: 300px;
}

.slider:hover {
  max-height: 600px;
  overflow-y: scroll;
}

.container {
  height: 0;
  padding-bottom: 20%;
  position: relative;
}

.container div {
  border: 2px dashed #ddd;
  height: 100%;
  left: 0;
  position: absolute;
  top: 0;
  width: 100%;
}

img {
  display: block;
  font-family: sans-serif;
  font-weight: 300;
  height: auto;
  line-height: 2;
  position: relative;
  text-align: center;
  width: 100%;
}

img::before {
  content: "We're sorry, the image below is broken :(";
  display: block;
  margin-bottom: 10px;
}

img::after {
  content: '(url: ' attr(src) ')';
  display: block;
  font-size: 12px;
}
