  plugins: [
    plugin(function({ matchVariant }) {
      matchVariant(
        'nth',
        (value) => {
          return `&:nth-child(${value})`;
        },
        {
          values: {
            1: '1',
            2: '2',
            3: '3',
          },
          sort(a, z) {
            return parseInt(a.value) - parseInt(z.value);
          },
        }
      );
    })
  ]
}

